const animItems=document.querySelectorAll(".anim-items"),langBtn=document.querySelector(".lang"),iconMenu=document.querySelector(".navigation__burger"),menuBody=document.querySelector(".navigation__list");function animateOnScroll(){for(let e=0;e<animItems.length;e++){const n=animItems[e],t=n.offsetHeight,o=offset(n).top,i=4;let s=window.innerHeight-t/i;t>window.innerHeight&&(s=window.innerHeight-window.innerHeight/i),pageYOffset>o-s&&pageYOffset<o+t?n.classList.add("active"):n.classList.remove("active")}}function offset(e){const n=e.getBoundingClientRect(),t=window.pageXOffset||document.documentElement.scrollLeft,o=window.pageXOffset||document.documentElement.scrollTop;return{top:n.top+o,left:n.left+t}}function openMenu(e){iconMenu.classList.toggle("open"),menuBody.classList.toggle("open")}function openLangList(e){langBtn.classList.add("open");e.target.classList.contains("lang")||langBtn.classList.remove("open")}document.addEventListener("DOMContentLoaded",(function(){window.addEventListener("scroll",animateOnScroll),langBtn.addEventListener("click",openLangList),iconMenu.addEventListener("click",openMenu)}));