const animItems=document.querySelectorAll(".anim-items");function animateOnScroll(){for(let e=0;e<animItems.length;e++){const t=animItems[e],n=t.offsetHeight,o=offset(t).top,i=4;let c=window.innerHeight-n/i;n>window.innerHeight&&(c=window.innerHeight-window.innerHeight/i),pageYOffset>o-c&&pageYOffset<o+n?t.classList.add("active"):t.classList.remove("active")}}function offset(e){const t=e.getBoundingClientRect(),n=window.pageXOffset||document.documentElement.scrollLeft,o=window.pageXOffset||document.documentElement.scrollTop;return{top:t.top+o,left:t.left+n}}window.addEventListener("scroll",animateOnScroll);const iconMenu=document.querySelector(".navigation__burger"),menuBody=document.querySelector(".navigation__list");iconMenu.addEventListener("click",e=>{iconMenu.classList.toggle("open"),menuBody.classList.toggle("open")});