const animItems=document.querySelectorAll(".anim-items");function animateOnScroll(){for(let e=0;e<animItems.length;e++){const n=animItems[e],t=n.offsetHeight,o=offset(n).top,i=4;let c=window.innerHeight-t/i;t>window.innerHeight&&(c=window.innerHeight-window.innerHeight/i),pageYOffset>o-c&&pageYOffset<o+t?n.classList.add("active"):n.classList.remove("active")}}function offset(e){const n=e.getBoundingClientRect(),t=window.pageXOffset||document.documentElement.scrollLeft,o=window.pageXOffset||document.documentElement.scrollTop;return{top:n.top+o,left:n.left+t}}window.addEventListener("scroll",animateOnScroll);const iconMenu=document.querySelector(".navigation__burger"),menuBody=document.querySelector(".navigation__list");iconMenu.addEventListener("click",e=>{iconMenu.classList.toggle("open"),menuBody.classList.toggle("open")});const langBtn=document.querySelector(".lang");langBtn.addEventListener("click",(function(){langBtn.classList.toggle("open")}));